import React, { useState } from "react";
import ExpenseForm from "./\bExpenseForm";
import "./NewExpense.css";

function NewExpense({ onAddExpense }) {
  const [addNewExpenseBtn, setAddNewExpenseBtn] = useState(false);
  const onSaveExpenseData = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    console.log("자식 컴포넌트에서 받은 expenseData : ", expenseData);

    onAddExpense(expenseData);
  };

  const onAddNewExpenseClick = () => {
    setAddNewExpenseBtn((prev) => !prev);
  };

  let expensesFormContent = (
    <div className="new-expense__actions">
      <button onClick={onAddNewExpenseClick}>Add New Expense</button>
    </div>
  );

  if (addNewExpenseBtn === true) {
    expensesFormContent = <ExpenseForm onSaveExpenseData={onSaveExpenseData} />;
  }

  return <div className="new-expense">{expensesFormContent}</div>;
}

export default NewExpense;
