import React, { useState } from "react";
import ExpenseForm from "./\bExpenseForm";
import "./NewExpense.css";

function NewExpense({ onAddExpense }) {
  const [isEditing, setIsEditing] = useState(false);
  const onSaveExpenseData = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    console.log("자식 컴포넌트에서 받은 expenseData : ", expenseData);

    onAddExpense(expenseData);
  };

  const onAddNewExpenseToggle = () => {
    setIsEditing((prev) => !prev);
  };

  let expensesFormContent = (
    <button onClick={onAddNewExpenseToggle}>Add New Expense</button>
  );

  if (isEditing === true) {
    expensesFormContent = (
      <ExpenseForm
        onSaveExpenseData={onSaveExpenseData}
        onCancleFormExpense={onAddNewExpenseToggle}
      />
    );
  }

  return <div className="new-expense">{expensesFormContent}</div>;
}

export default NewExpense;
